import React, { Component } from 'react';
import axios from 'axios';

class TodoList extends Component {
    state = {
        id: '',
        description: '',
        tasks: []
    }

    getList = () => {
        axios.get('/tasks/all')
            .then(res => {
            this.setState({ tasks: res.data.data });
            })
            .catch(err => console.log(err));
    }

    componentDidMount(){
        this.getList();
    }

    addTask = (description) => {
    axios.post('/tasks/add', {
        description: description
    })
    .then(res => {
        console.log(res);
    })
    .catch(err => console.log(err));
    }

    deleteTask = (id) => {
    axios.delete(`/tasks/${id}/delete`)
        .then(res => {
        console.log(res);
        })
        .catch(err => console.log(err));
    }

    markAsDone = (id) => {
    axios.put(`/tasks/${id}/done`, {
        status: 'done'
    })
        .then(res => {
        console.log(res);
        })
        .catch(err => console.log(err));
    }

    // onSubmit = e => {
    //     e.preventDefault()
    //     this.addTask(this.state.description).then(() => {
    //         this.
    //     })
    // }

    render(){    
        return (
            // <form>

            // </form>
            <table border="1">
            <thead>
                <th>#</th>
                <th>Task</th>
                <th></th>
            </thead>
            <tbody>
                {this.state.tasks.map((todo, i) => {
                return (
                    <tr key={i} style={{backgroundColor: todo.status == 'pending' ? 'green' : 'grey'}}>
                        <td>#{i}</td>
                        <td>{todo.description}</td>
                    {/* <td>
                        {todo.status == 'pending' && (
                        <input type="button" value="terminado?" onClick={(event) => markAsDone(event, this.id)}/>
                        )}
                    </td> */}
                    </tr>
                )
                })}
            </tbody>
            </table>
        );
    }
}

export default TodoList;
