import React, { Component } from 'react';
import './App.css';
import axios from 'axios';

import TodoList from './components/todos/TodoList'
import { markAsDone } from '../../server/models/Task';

class App extends Component {

  state = {
    tasks: []
  }

  componentDidMount(){
    axios.get('/tasks/all')
      .then(res => {
        this.setState({ tasks: res.data.data });
      })
      .catch(err => console.log(err));
  }

  addTask = (description) => {
    axios.post('/tasks/add', {
      description: description
    })
    .then(res => {
      console.log(res);
    })
    .catch(err => console.log(err));
  }

  deleteTask = (id) => {
    axios.delete(`/tasks/${id}/delete`)
      .then(res => {
        console.log(res);
      })
      .catch(err => console.log(err));
  }

  markAsDone = (id) => {
    axios.put(`/tasks/${id}/done`, {
      status: 'done'
    })
      .then(res => {
        console.log(res);
      })
      .catch(err => console.log(err));
  }

    render() {
      return(
        <div>
          <TodoList tasks={this.state.tasks} markAsDone={markAsDone}/>
        </div>
      )
    };
}

export default App;
