import React, { Component } from 'react';
import axios from 'axios';

class Todo extends Component {
    state = {

    }

    showSingle = (id) => {
        axios.get(`/tasks/${id}`)
            .then(res => {
                this.setState({task: res.data.data})
            })
            .catch(err => console.log(err));
    }

    onShow = (value, e) => {
        e.preventDefault()
        this.showSingle(value)
    }
}

export default Todo;
