import React from 'react';

function TodoList(props) {

    // const handleMarkAsDone = (event, index) => {
    //     props.markAsDone(index);
    // }

    const tasks = props.tasks;
    const listFood = tasks.map((value, index) => {
        return (
            <table border="1">
            <thead>
                <th>#</th>
                <th>Task</th>
                <th></th>
            </thead>
            <tbody>
                {listFood.map((todo, i) => {
                return (
                    <tr key={i} style={{backgroundColor: todo.status == 'pending' ? 'green' : 'grey'}}>
                    <td>#{i}</td>
                    <td>{todo.description}</td>
                    <td>
                        {/* {todo.status == 'pending' && (
                        <input type="button" value="terminado?" onClick={(event) => handleMarkAsDone(event, i)}/>
                        )} */}
                    </td>
                    </tr>
                )
                })}
            </tbody>
            </table>
        );
    });
}
export default TodoList;
