import React, { Component } from 'react';
import axios from 'axios';

class TodoList extends Component {
    constructor(){
        this.onChange = this.onChange.bind(this)
    }
    state = {
        id: '',
        description: '',
        tasks: []
    }
    getList = () => {
        axios.get('/tasks/all')
            .then(res => {
            this.setState({ tasks: res.data.data });
            })
            .catch(err => console.log(err));
    }

    addTask = (description) => {
    axios.post('/tasks/add', {
        description: description
    })
    .then(res => {
        console.log(res);
    })
    .catch(err => console.log(err));
    }

    deleteTask = (id) => {
    axios.delete(`/tasks/${id}/delete`)
        .then(res => {
        console.log(res);
        })
        .catch(err => console.log(err));
    }

    markAsDone = (id) => {
    axios.put(`/tasks/${id}/done`, {
        status: 'done'
    })
        .then(res => {
        console.log(res);
        })
        .catch(err => console.log(err));
    }

    componentDidMount(){
        this.getList();
    }

    onChange = e => {
        this.setState({
            [e.target.name]: e.target.value
        })
    }

    onSubmit = e => {
        e.preventDefault()
        this.addTask(this.state.description).then(() => {
            this.getList()
        })
        this.setState({
            description: ''
        })
    }

    onUpdate = (value, e) => {
        e.preventDefault()
        this.markAsDone(value)

        let data = [...this.state.tasks]
        data.filter(function(task, index) {
            if(task.id === value){
                task.status = 'done'
            }
            return true
        })
        this.setState({ tasks: [...data]})
    }

    onDelete = (value, e) => {
        e.preventDefault()
        this.deleteTask(value)

        let data = [...this.state.tasks]
        data.filter(function(task, index) {
            if(task.id === value){
                data.splice(index, 1)
            }
            return true
        })
        this.setState({ tasks: [...data]})
    }

    render(){    
        return (
            <>
            <div>
                <form onSubmit={this.onSubmit}>
                    <div>
                        <label for="description">TODO</label>
                        <input 
                            type="text" 
                            id="description" 
                            name="description"
                            value={this.state.value || ''}
                            onChange={this.onChange.bind(this)}
                        />
                        <button
                            type="submit"
                            onClick={this.onSubmit.bind(this)}>
                                Submit
                        </button>
                    </div>
                </form>
            </div>
            <div>
                <table border="1">
                <thead>
                    <th>#</th>
                    <th>Task</th>
                    <th></th>
                </thead>
                <tbody>
                    {this.state.tasks.map((todo, i) => {
                    return (
                        <tr key={i} style={{backgroundColor: todo.status == 'pending' ? 'green' : 'grey'}}>
                            <td>#{i}</td>
                            <td>{todo.description}</td>
                            <td>
                                <button
                                    href=""
                                    onClick={this.onUpdate.bind(this, todo.id)}
                                >
                                        Terminado
                                </button>
                            </td>
                            <td>
                                <button
                                    href=""
                                    onClick={this.onDelete.bind(
                                        this,
                                        todo.id
                                    )}
                                >
                                        Delete
                                </button>
                            </td>
                        {/* <td>
                            {todo.status == 'pending' && (
                            <input type="button" value="terminado?" onClick={(event) => markAsDone(event, this.id)}/>
                            )}
                        </td> */}
                        </tr>
                    )
                    })}
                </tbody>
                </table>
            </div>
            </>
        );
    }
}

export default TodoList;
