import React from 'react';

function TodoList(props) {

    const tasks = props.tasks;
    tasks.map((todo, i) => {
        return (
            <table border="1">
                <thead>
                <th>#</th>
                <th>Task</th>
                <th></th>
            </thead>
            <tbody>
            <tr key={i} style={{backgroundColor: todo.status == 'pending' ? 'green' : 'grey'}}>
              <td>#{i}</td>
              <td>{todo.description}</td>
            </tr>
            </tbody>
            </table>
        );
    });

}
export default TodoList;
