import React, { Component } from 'react';
import './App.css';
import axios from 'axios';

import TodoList from './components/todos/TodoList'

class App extends Component {

  state = {
    tasks: []
  }

  componentDidMount(){
    axios.get('/tasks/all')
      .then(res => {
        this.setState({ tasks: res.data.data });
      })
      .catch(err => console.log(err));
  }

  addTask = (description) => {
    axios.post('/tasks/add', {
      description: description
    })
    .then(res => {
      console.log(res);
    })
    .catch(err => console.log(err));
  }

  deleteTask = (id) => {
    axios.delete(`/tasks/${id}/delete`)
      .then(res => {
        console.log(res);
      })
      .catch(err => console.log(err));
  }

  markAsDone = (task) => {
    let cTodos = Object.assign([], tasks);
    cTodos[task].status = 'done';
    setTodos(cTodos);
  }

    render() {
      return(
        <div>
          <TodoList tasks={this.state.tasks}/>
        </div>
      )
    };
}

export default App;
